using System;using UnityEngine;using UnityEngine.UI;public class PickUpItem : MonoBehaviour{    public GameObject pickUp;    public Transform mainCam;    public int dist;    public void Update()    {        if (Input.GetKeyDown(KeyCode.Q)) {            RaycastHit hit; // Cast a ray from the cursor position using the camera's forward direction.            if (Physics.Raycast(mainCam.position, mainCam.TransformDirection(Vector3.forward), out hit, dist) && hit.transform.CompareTag("PickUpItem")) {  // Check if the ray hits an object within the maximum distance.                AddToPickup(hit.transform.gameObject);            }        }    }    private void AddToPickup(GameObject item)    {        if(pickUp != null)            DropBackToWorld(pickUp);        pickUp = item;        item.SetActive(false);    }    private void DropBackToWorld(GameObject item)    {                item.transform.position = mainCam.position + mainCam.forward * item.GetComponent<Renderer>().bounds.extents.magnitude;        item.SetActive(true);        pickUp = null;    }}